sql.if:
where  

	title regexp #{word}


	content regexp #{word}


	memberId regexp #{word}


	title regexp #{word} 
	or content regexp #{word}


	title regexp #{word} 
	or memberId regexp #{word}


	content regexp #{word} 
	or memberId regexp #{word}


	title regexp #{word} 
	or content regexp #{word} 
	or memberId regexp #{word}
